HallCallProcessThread::Execute() 
{ 
 hallCallListSemaphore->signal(); 
 MutexLock(mHallCallListMutex); 
 Hallcall hallcall = hallcallList_->pop(); 
 OneHallcallThread thread(hallcall, hallcallList_); 
