class CardDeck 
{ 
    vector  cardDecks_;  
    public: 
        CardDeck() 
        { 
            CardSuite suite; 
            CardNumber cNumber; 
            for (suite = CARD_CLUB; suite != CARD_DIAMOND + 1; suite = static_cast(suite + 1)) 
            { 
                for (cNumber = CARD_ONE; cNumber != CARD_KING + 1; cNumber = static_cast(cNumber + 1)) 
                { 
                    OneCard card = OneCard(suite, cNumber); 
                    cardDecks_.push_back(card); 
                } 
            } 
        } 

        void RandomizeCards() 
        { 
            std::random_shuffle(cardDecks_.begin(), cardDecks_.end()); 
        } 

        void Decompile()  
        { 
            vector :: iterator iter = cardDecks_.begin(); 
            while (iter != cardDecks_.end()) 
            { 
                (*iter).Decompile(); 
                iter++; 
            } 
        } 


}; 


