 private static void sortMap(Map map) { 
  for (int i = 0; i < map.size(); i++) { 
   for (int j = i + 1; j < map.size(); j++) { 
    int temp = map.get(i); 
    if (map.get(j) < temp) { 
     map.put(i, map.get(j)); 
     map.put(j, temp); 
    } 
   } 
  } 
 }

